import { Link } from 'react-router-dom'
import './ItemCard.css'
import { useState } from 'react'
import { useFetch } from '../../Utils/useFetch'
import { useContext } from 'react'
import { ShopContext } from '../Context/ShopContext'


export const ItemCard = ( { category, id, thumbnail, title, brand, price, rating } ) =>{

    const { products } = useFetch()
    const { addToCart } = useContext(ShopContext)

    console.log(products[id])

    /*
    const [productsIDs, setProductsIDs] = useState({
        category: "", 
        id: "", 
        thumbnail: "", 
        title: "", 
        brand: "", 
        price: "", 
        rating: "",
    })

    //console.log(productsIDs)

    const handleSubmitCart = (e) =>{
        e.preventDefault()

        const guardarLocalStorage = (id) => localStorage.setItem(`produtosCarrinho`, JSON.stringify(id))
        const pegarLocalStorage = () => JSON.parse(localStorage.getItem('produtosCarrinho')) ?? []
       
        const dbCLient = pegarLocalStorage()
        setProductsIDs({category, id, thumbnail, title, brand, price, rating})
        console.log(productsIDs)
        //dbCLient.push(productsIDs)
        guardarLocalStorage(dbCLient)
        /*
        if(dbCLient[id] === id){
            console.log('Esse número já existe')
            //document.getElementById('btn-addFav').disabled = true
            dbCLient.pop(id)
        }
        

    }

    const handleSubmitFav = (e) =>{
        e.preventDefault()

        const guardarLocalStorage = (id) => localStorage.setItem(`produtosFavoritados`, JSON.stringify(id))
        const pegarLocalStorage = () => JSON.parse(localStorage.getItem('produtosFavoritados')) ?? []
       
        const dbCLient = pegarLocalStorage()
        dbCLient.push(id)
        guardarLocalStorage(dbCLient)
    }
    */

    return(
        <div className='ItemCard'>
                <div className='box-itemCard'>
                    <Link id='Link' to={`/products/${id}`}>
                        <p id='category'>{category}</p>
                        <div>
                            <div>
                                <img src={thumbnail} alt="" id='img'/>
                            </div>
                            <div id='product-infos'>
                                <p id='title'>{title}</p>
                                <span>{brand}</span>
                                <p id='price'>R$: {price},00</p>
                                <p>Avaliação: {rating}</p>
                            </div>
                        </div>
                        </Link>
                    <footer id='btns'>
                        <button id='btn-addFav' /*onClick={handleSubmitFav}*/ disabled>Adicionar aos favoritos</button>
                        <button id='btn-addCart' /*onClick={handleSubmitCart}*/>Adicionar ao Carrinho</button>
                    </footer>
                </div>
        </div>
    )
}
