import { useEffect, useState } from 'react';
import './App.css';
import { Header } from './Components/Header/Header';
import { Products } from './Components/Products/Products';
import { Loading } from './Components/Loading/Loading';
import { CartPage } from './Components/Cart/CartPage';

function App() {

  const [products, setProducts] = useState([])
  const [loading, setLoading] = useState(true)
  const cardProducts = document.querySelectorAll('.box-itemCard')
  const cartButton = document.getElementById('cartButton')

  useEffect( () =>{
      fetch('https://dummyjson.com/products')
      .then(res => res.json())
      .then(json => setProducts(json.products))
      setLoading(false)
  }, [])

  const openProductPage = () =>{
    const arrCDProducts = Array.from(cardProducts)

    arrCDProducts.forEach( (cardProduct) =>{
      cardProduct.addEventListener('click', (e) =>{
        console.log(e.target)
      })
      //console.log(cardProduct)
    })
  }

  const openCartPage = () =>{
    console.log('')
  }

  openProductPage()
  cartButton.addEventListener('click', openCartPage)

  return (

    <div className="App">
        {loading ? <Loading /> : 
          <div id='main-store'>
            <Header />
            <Products products={products}/>
          </div>}

        <div id='product-page'>
          <Header />
        </div>

        <div id='cartPage'>
          <CartPage />
        </div>
    </div>
  );
}

export default App;
