import { useContext, useEffect, useState } from 'react'
import { useFetch } from '../../Utils/useFetch'
import { ShopContext } from '../Context/ShopContext'
import { CartProduct } from './CartProduct'

export const CartPage = ( { } ) =>{  

    const { products } = useFetch()
    const { cartItems } = useContext(ShopContext)

    const [priceProducts, setPriceProducts] = useState(0)

    /*
    useEffect( () =>{
        setPriceProducts(productPrice)
    }, [productPrice])

    console.log(priceProducts)


    return(

        <div id='main-cart'>
            
            <p>Meu Carrinho</p>
            <hr />

            <div id='cart-item-finish'>

                <div id='product'>
                
                    {products.map( (product) =>{
                        if(cartItems[product.id] > 0){
                            return <CartProduct key={product.id} product={product}/>
                        }
                        return null
                    })}

                    {Object.values(cartItems).every(quantity => quantity === 0) && (
                        <h1>Nenhum produto adicionado ao carrinho :(</h1>
                    )}
                </div>

                <div id='resumo'>
                    <div id='boxResumoDoPedido'>
                        <p id='resumoDoPedido'>Resumo do Pedido</p>
                        <hr />
                        <div id='subTotal'>
                            <p>Subtotal</p>
                            <p>R$: {productPrice}</p>
                        </div>
                        <hr />
                        <div id='entrega'>
                            <p>Entrega</p>
                            <p>R$: {priceProducts} </p>
                        </div>
                        <hr />
                        <div id='cupom'>
                            <p>Cupom de desconto</p>
                            <p>R$: {priceProducts}</p>
                        </div>
                        <hr />
                        <div id='total'>
                            <div id='valorTotal'>
                                <p>Total</p>
                                <p>R$: {priceProducts}</p>
                            </div>
                            <p id='valorComDesconto'>Valor com desconto</p>
                            <p id='spanValorComDesconto'>Valor com <span>10% de desconto</span> no Boleto ou PIX</p>
                        </div>
                    </div>

                    <div id='btnsResumoPedido'>
                        <button id='adicionarMaisProdutos'>Adicionar mais produtos</button>
                        <button id='fecharPedido'>Fechar pedido</button>
                    </div>
                </div>

            </div>
        </div>
    )
}


