export const CartProduct = ( { product } ) =>{

    //console.log(product)

    const { removeFromCart } = useContext(ShopContext)
    const [priceProducts, setPriceProducts] = useState(0)

    const addPriceProducts = () =>{
        <CartPage productPrice={priceProducts}/>
