import { useContext, useEffect, useState } from 'react'
import { useFetch } from '../../Utils/useFetch'
import { ShopContext } from '../Context/ShopContext'
import { CartProduct } from './CartProduct'
import { Values } from './Values'

export const CartPage = ( { } ) =>{  

    const { products } = useFetch()
    const { cartItems } = useContext(ShopContext)

    const [priceProducts, setPriceProducts] = useState(0)

    const resumo = document.getElementById('resumo')

    /*
    useEffect( () =>{
        setPriceProducts(productPrice)
    }, [productPrice])

    console.log(priceProducts)

    */
    return(

        <div id='main-cart'>
            
            <p>Meu Carrinho</p>
            <hr />

            <div id='cart-item-finish'>

                <div id='product'>
                
                    {products.map( (product) =>{
                        if(cartItems[product.id] > 0){
                            return <CartProduct key={product.id} product={product}/>
                        }
                        return null
                    })}

                    {Object.values(cartItems).every(quantity => quantity === 0) && (
                        <h1 id='NAProduct'>Nenhum produto adicionado ao carrinho :(</h1>
                    )}
                </div>

                <Values />

            </div>

            <div id='cupom-entrega'>
                <div id='frete'>

                    <div id='input-btnFrete'>
                        <h3 id='h3Entrega'>Entrega </h3>
                        <input type="number" id='inputEntrega' required/>
                        <input type='button' id='buttonEntrega' value="Calcular"/>
                    </div>

                    <div id='escolherFrete'>
                        <input type="checkbox" />

                        <section>
                            <p>ECONÔMICO: R$:20,00</p>
                            <p>Prazo mínimo: 6 dias úteis</p>
                            <p>Prazo Máximo: 13 dias úteis</p>
                        </section>
                    </div>
                </div>

                <div id='cupomDeDesconto'>
                    <h3 id='h3Cupom'>Cupom de desconto </h3>
                    <input type="text" id='inputCupom'/>
                    <input type='button' id='buttonCupom' value="Aplicar"/>
                </div>
            </div>
        </div>
    )
}


