import { useEffect, useState } from 'react';
import './App.css';
import { Header } from './Components/Header/Header';
import { Products } from './Components/Products/Products';
import { Loading } from './Components/Loading/Loading';
import { CartPage } from './Components/Cart/CartPage';

import { BrowserRouter, Routes, Route } from 'react-router-dom';

function App() {

  const [products, setProducts] = useState([])
  const [loading, setLoading] = useState(true)
  const cardProducts = document.querySelectorAll('.box-itemCard')

  useEffect( () =>{
      fetch('https://dummyjson.com/products')
      .then(res => res.json())
      .then(json => setProducts(json.products))
      setLoading(false)
  }, [])

  const openProductPage = () =>{
    const arrCDProducts = Array.from(cardProducts)

    arrCDProducts.forEach( (cardProduct) =>{

      //console.log(cardProduct)

      cardProduct.addEventListener('click', (e) =>{
        console.log(e.target)
      })
      //console.log(cardProduct)
    })
  }

  const openCartPage = (e) =>{
    e.preventDefault()
    document.getElementById('main-store').style = 'display: none;'
    document.getElementById('cart-page').style = 'display: block;'
    console.log('Botão clicado')
  }

  return (

    <div className="App">

      <BrowserRouter>

        <Header paginaCarrinho={openCartPage} />
        <Products products={products} paginaProduto={openProductPage}/>

        <Routes>

        </Routes>
      </BrowserRouter>

      {/*
        {loading ? <Loading /> : 
          <div id='main-store'>
            <Header paginaCarrinho={openCartPage} />
            <Products products={products} paginaProduto={openProductPage}/>
          </div>}

        <div id='product-page'>
          <Header paginaCarrinho={openCartPage}/>
        </div>

        <div id='cart-page'>
          <Header />
          <CartPage products={products}/>
        </div>
      */}  
    </div>
  );
}

export default App;
