import './Products.css'
import { ItemCard } from '../ItemCard/ItemCard'
import { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { useFetch } from '../../Utils/useFetch'

export const Products = ( {} ) =>{
    const navigate = useNavigate()
    const [query, setQuery] = useState()

    const { products } = useFetch()
    const [filteredProducts, setFilteredProducts] = useState([])

    const handleSubmit = (e) =>{
        e.preventDefault()

        setFilteredProducts(products.filter( (product) => product.title.toLowerCase().includes(query)))

        /*const filteredProducts = products.filter( (product) => product.title.toLowerCase().includes(query))
        */ 
        //navigate("search?q=" + query)
    }


    console.log()

    return(
        <div className='main'>
            <div id='form'>
                <form onSubmit={handleSubmit}>
                    <input type="text" required onChange={ (e) => setQuery(e.target.value.toLowerCase())} id='input-txt'/>
                    <input type="submit" value="Buscar" id='input-sbmt'/>
                </form>
            </div>

            <div id='products'>
                {filteredProducts == [] ? products.map( product => (
                    <ItemCard
                        key={product.id}
                        category={product.category}
                        id={product.id}
                        thumbnail={product.thumbnail}
                        title={product.title}
                        brand={product.brand}
                        price={product.price}
                        rating={product.rating}
                    />
                )) : filteredProducts.map( ftProduct => (
                    <ItemCard
                        key={ftProduct.id}
                        category={ftProduct.category}
                        id={ftProduct.id}
                        thumbnail={ftProduct.thumbnail}
                        title={ftProduct.title}
                        brand={ftProduct.brand}
                        price={ftProduct.price}
                        rating={ftProduct.rating}
                    />
                ))}
            </div>
        </div>
    )
}
