import './Products.css'
import { ItemCard } from '../ItemCard/ItemCard'
import { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { useFetch } from '../../Utils/useFetch'

export const Products = ( {/* products */} ) =>{
    const navigate = useNavigate()
    const [query, setQuery] = useState()

    const { products } = useFetch()

    const handleSubmit = (e) =>{
        e.preventDefault()
        
        const filteredProducts = products.filter( (product) => product.title.toLowerCase().includes(query))

        //navigate("search?q=" + query)
    }

    

    return(
        <div className='main'>
            <div id='form'>
                <form onSubmit={handleSubmit}>
                <input type="text" value={query} required onChange={ (e) => setQuery(e.target.value.toLowerCase())} id='input-txt'/>
                <input type="submit" value="Buscar" id='input-sbmt'/>
                </form>
            </div>

            <div id='products'>
                {filteredProducts.map( fpproduct => (
                    <ItemCard
                        key={fpproduct.id}
                        category={fpproduct.category}
                        id={fpproduct.id}
                        thumbnail={fpproduct.thumbnail}
                        title={fpproduct.title}
                        brand={fpproduct.brand}
                        price={fpproduct.price}
                        rating={fpproduct.rating}
                    />
                ))}
            </div>
        </div>
    )
}
