import './Products.css'
import { ItemCard } from '../ItemCard/ItemCard'
import { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { useFetch } from '../../Utils/useFetch'

export const Products = ( {/* products */} ) =>{
    const navigate = useNavigate()
    const [query, setQuery] = useState()

    const { products } = useFetch()

    const [filteredProducts, setFilteredProducts] = useState(products)

    CONSOL

    const handleSubmit = (e) =>{
        e.preventDefault()

        setFilteredProducts( products.filter( (product) => product.title.toLowerCase().includes(query)) )
        //navigate("search?q=" + query)
    }

    console.log(filteredProducts)

    return(
        <div className='main'>
            <div id='form'>
                <form onSubmit={handleSubmit}>
                <input type="text" value={query} required onChange={ (e) => setQuery(e.target.value.toLowerCase())} id='input-txt'/>
                <input type="submit" value="Buscar" id='input-sbmt'/>
                </form>
            </div>

            <div id='products'>
                {
                    filteredProducts.map(product => (
                        <div>
                            <li key={product.id}></li>
                            <li>{product.title}</li>
                            <li>{product.brand}</li>
                        </div>
                        
                    ))
                }
                {/*products.map( product => (
                    <ItemCard
                        key={product.id}
                        category={product.category}
                        id={product.id}
                        thumbnail={product.thumbnail}
                        title={product.title}
                        brand={product.brand}
                        price={product.price}
                        rating={product.rating}
                    />
                ))*/}
            </div>
        </div>
    )
}
