import { useContext, useEffect } from 'react'
import { useFetch } from '../../Utils/useFetch'
import './CartPage.css'
import { ShopContext } from '../Context/ShopContext'

export const CartPage = ( { productsIDs } ) =>{    

    const { products } = useFetch()
    const { cartItems } = useContext(ShopContext)

    console.log(cartItems)

    //console.log(products)

    useEffect( () =>{
        const guardarLocalStorage = (id) => localStorage.setItem(`produtosCarrinho`, JSON.stringify(id)) 
        dbCLient.push(cartItems)
        guardarLocalStorage(cartItems)
    }, [cartItems])

    return(
        <div id='main-cart'>
            <p>Meu Carrinho</p>
            {products.map( (product) => (
                <div id='product-cart' key={product.id}>
                    <div id='aaa'>
                        <input type="checkbox" name="" id="input-check" />
                    <div>
                        <img src={product.thumbnail} alt="" id='product-cart-img'/>
                    </div>
                    <div>
                        <p id='cart-pdtTitle'>{product.title}</p>

                    {/* Quantidade: <button id='minus'>-</button>
                    <input type="number" id="qtProducts" />
                    <button id='plus'>+</button> */}
                    </div>
                </div>

                <div id='price-delete'>
                <p>R$:{product.price},00</p>
                <button>
                    <span className="material-symbols-outlined">
                        delete
                    </span>
                </button>
            </div>
            </div>
        ))}
           
        </div>
    )
}


