import './Products.css'
import { ItemCard } from '../ItemCard/ItemCard'
import { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { useFetch } from '../../Utils/useFetch'

export const Products = ( {/* products */} ) =>{
    const navigate = useNavigate()
    const [query, setQuery] = useState()

    const { products } = useFetch()

    const handleSubmit = (e) =>{
        e.preventDefault()

        

        //navigate("search?q=" + query)
    }

    const lwcase = query.toLowerCase()

        const filteredProducts = products.filter( (product) => product.title.toLowerCase().includes(lwcase))

        console.log(filteredProducts)

    return(
        <div className='main'>
            <div id='form'>
                <form onSubmit={handleSubmit}>
                <input type="text" required onChange={ (e) => setQuery(e.target.value.toLowerCase())} id='input-txt'/>
                <input type="submit" value="Buscar" id='input-sbmt'/>
                </form>
            </div>

            <div id='products'>

                {products.map( product => (
                    <ItemCard
                        key={product.id}
                        category={product.category}
                        id={product.id}
                        thumbnail={product.thumbnail}
                        title={product.title}
                        brand={product.brand}
                        price={product.price}
                        rating={product.rating}
                    />
                ))}
            </div>
        </div>
    )
}
