import './Products.css'
import { ItemCard } from '../ItemCard/ItemCard'
import { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { useFetch } from '../../Utils/useFetch'

export const Products = ( {/* products */} ) =>{
    const navigate = useNavigate()
    const [query, setQuery] = useState()

    const { products } = useFetch()
    const [filteredProducts, setFilteredProducts] = useState([])
    const [filteredProductsBool ,setFilteredProductsBool] = useState(true)

    const handleSubmit = (e) =>{
        e.preventDefault()

        setFilteredProducts( products.filter( (product) => product.title.toLowerCase().includes(query)) )

        setFilteredProductsBool(true)

        //navigate("search?q=" + query)
    }
    

    return(
        <div className='main'>
            <div id='form'>
                <form onSubmit={handleSubmit}>
                
                </form>

                <input type="text" value={query} required onChange={ (e) => setQuery(e.target.value.toLowerCase())} id='input-txt'/>
                <input type="submit" value="Buscar" id='input-sbmt'/>
            </div>

            <div id='products'>
                {!filteredProductsBool ? products.map( product => (
                    <ItemCard
                        key={product.id}
                        category={product.category}
                        id={product.id}
                        thumbnail={product.thumbnail}
                        title={product.title}
                        brand={product.brand}
                        price={product.price}
                        rating={product.rating}
                    />
                )) : fpproduct}
            </div>
        </div>
    )
}
