import { useContext } from 'react'
import './Favorites.css'
import { ShopContext } from '../Context/ShopContext'
import { useFetch } from '../../Utils/useFetch'

export const Favorites = () =>{

    const { products } = useFetch()
    const { removeFromFavs } = useContext(ShopContext)

    return(
        <div id="mainFavorites">

{products.map( (product) =>{
                        if(cartItems[product.id] > 0){
                            return <CartProduct key={product.id} product={product}/>
                        }
                        return null
                    })}

                    {Object.values(cartItems).every(quantity => quantity === 0) && (
                        <h1 id='NAProduct'>Nenhum produto adicionado ao carrinho :(</h1>
                    )}

            <div>
                <button id='btnLixeiraFav' /*onClick={() => removeFromCart(product.id, product.price)}*/>
                    <span className="material-symbols-outlined" id='lixeiraFav'>
                        delete
                    </span>
                </button>
            </div>

            <div id="divFavoriteIMG">
                <img src="" alt="" id="favoriteIMG"/>
            </div>

            <div id="FavoriteItemDescription">
                <h1>Título</h1>

                <p>Preço</p>
                <p>Parcelamento</p>
                <p>Avaliação</p>

                <div>
                    <button onClick={() => removeFromFavs()}>Adicionar ao Carrinho</button>
                    <button>Comprar Agora</button>
                </div>
            </div>
        </div>
    )
}
