import { useState } from 'react'
import './ItemCard.css'
import { ProductPage } from '../ProductPage/ProductPage'

export const ItemCard = ( { category, id, thumbnail, title, brand, price, rating } ) =>{

    //console.log(category, id, img, title, brand, price, rating)

    const [productPage, setProductPage] = useState()
    const [productPageIsOpen, setProductPageOpen] = useState(false)

    const handleRemove = (id) =>{
        console.log(id)
        setProductPage(id)
        setProductPageOpen(true)
    }

    return(
        <div className='ItemCard' onClick={() => handleRemove(id)}>
            {productPageIsOpen === true}{
                <ProductPage />
            }else{}
            <div className='box-itemCard'>
                <p id='category'>{category}</p>
                <div>
                    <div>
                        <img src={thumbnail} alt="" id='img'/>
                    </div>
                    <div id='product-infos'>
                        <p id='title'>{title}</p>
                        <span>{brand}</span>
                        <p id='price'>R$: {price},00</p>
                        <p>Avaliação: {rating}</p>
                    </div>
                </div>
                <footer id='btns'>
                        <button id='btn-addFav'>Adicionar aos favoritos</button>
                        <button id='btn-addCart'>Adicionar ao Carrinho</button>
                </footer>
            </div>
        </div>
    )
}
