import { createContext, useEffect, useState } from "react"
import { useFetch } from "../../Utils/useFetch"

export const ShopContext = createContext(null)

export const ShopContextProvider = (props) =>{
    const { products } = useFetch()

    const getDefaultCart = () =>{

        let cart = {}
    
        for( let i = 1; i < products.length + 1; i++){
            cart[i] = 1
        }

        //console.log(cart)
    
        return cart
    }

    const [cartItems, setCartItems] = useState(getDefaultCart())

    //console.log(cartItems)

    const addToCart = (itemId) => {
        //console.log(itemId)
        setCartItems((prev) => ({ ...prev, [itemId]: itemId}));
    };

    const removeFromCart = (itemId) =>{
        setCartItems(
            (prev) => ({...prev, [itemId]: prev[itemId] - 1})
        )
    }

    const contextValue = { cartItems, addToCart, removeFromCart}

    useEffect( () =>{
        const guardarLocalStorage = (id) => localStorage.setItem(`produtosCarrinho`, id)
        const pegarLocalStorage = () => JSON.parse(localStorage.getItem('produtosCarrinho')) ?? []
       
        const dbCLient = pegarLocalStorage()
        dbCLient.push(cartItems)
        guardarLocalStorage(dbCLient)
    }, [cartItems])

    return <ShopContext.Provider value={contextValue}>{props.children}</ShopContext.Provider>
}
