import { useEffect, useState } from 'react';
import './App.css';
import { Header } from './Components/Header/Header';
import { Products } from './Components/Products/Products';
import { Loading } from './Components/Loading/Loading';
import { CartPage } from './Components/Cart/CartPage';

import { BrowserRouter, Routes, Route } from 'react-router-dom';

function App() {

  const [products, setProducts] = useState([])
  const [loading, setLoading] = useState(true)
  const cardProducts = document.querySelectorAll('.box-itemCard')

  useEffect( () =>{
      fetch('https://dummyjson.com/products')
      .then(res => res.json())
      .then(json => setProducts(json.products))
      setLoading(false)
  }, [])

  const openProductPage = () =>{
    const arrCDProducts = Array.from(cardProducts)


    arrCDProducts.map( (cardProduct) =>{

      //console.log(cardProduct)

      cardProduct.addEventListener('click', (e) =>{
        //console.log(e)
      })
      //console.log(cardProduct)
    })
  }

  return (

    <div className="App">

      <BrowserRouter>

        <Header/>

        <Routes>
          <Route path='/' element={<Products products={products} paginaProduto={openProductPage}/>}/>
          <Route path='/cart' element={<CartPage products={products}/>}/>
        </Routes>
      </BrowserRouter>

      {/*
        {loading ? <Loading /> : 
          <div id='main-store'>
            <Header paginaCarrinho={openCartPage} />
            <Products products={products} paginaProduto={openProductPage}/>
          </div>}

        <div id='product-page'>
          <Header paginaCarrinho={openCartPage}/>
        </div>

        <div id='cart-page'>
          <Header />
          <CartPage products={products}/>
        </div>
      */}  
    </div>
  );
}

export default App;
