import { useContext, useState } from "react"
import { ShopContext } from "../Context/ShopContext"

export const Values = ( { CEP, cupom } ) =>{

    const { priceProducts } = useContext(ShopContext) 

    const inputCheck = document.getElementById()

    const cep = CEP
    //const cupomDeDesconto = cupom

    const [desconto2, setDesconto] = useState(0)

    let percentual = 0.10;
    let desconto = priceProducts * percentual;
    let novoPreco = priceProducts - desconto;

    const funcaoAleatoria = () =>{
        if(cupom.toUpperCase() === 'LUTZ10'){
            return novoPreco
        } 
          
        return 0
    }


    //console.log(cep)
    //console.log(cupomDeDesconto)


    return(
        <div id='resumo'>
            <div id='boxResumoDoPedido'>

                <p id='resumoDoPedido'>Resumo do Pedido</p>

                <hr />
                <div id='subTotal'>
                    <p>Subtotal</p>
                    <p>R$: {priceProducts}</p>
                </div>
                <hr />

                <div id='entrega'>
                    <p>Entrega</p>
                    <p>R$: {priceProducts}</p>
                </div>
                <hr />

                <div id='cupom'>
                    <p>Cupom de desconto</p>
                    <p>R$: {funcaoAleatoria()}</p>
                </div>
                <hr />

                <div id='total'>
                    <div id='valorTotal'>
                        <p>Total</p>
                        <p>R$: {priceProducts}</p>
                    </div>
                    
                    <div id="descontos">
                        <p id="valorOriginal">R$: {priceProducts}</p>
                        <p id='valorComDesconto'>R$: {novoPreco}</p>
                    </div>
                    <p id='spanValorComDesconto'>Valor com <span>10% de desconto</span> no Boleto ou PIX</p>

                </div>
            </div>

            <div id='btnsResumoPedido'>
                <button id='adicionarMaisProdutos'>Adicionar mais produtos</button>
                <button id='fecharPedido'>Fechar pedido</button>
            </div>

        </div>
    )
}

/*
R$: {cupomDeDesconto > 0 ? (
    novoPreco
) : (
    priceProducts
)}
*/
